/**
 * @brief read voltage
 *
 * @param[in] port_num: adc port number
 * @param[out] buff: points to the list of calculate voltage,bat : mv
 * @param[in] len:  buff len
 * @return OPRT_OK on success. Others on error, please refer to tuya_error_code.h
 *
 */
OPERATE_RET tkl_adc_read_voltage(TUYA_ADC_NUM_E port_num, int32_t *buff, uint16_t len)
{
    // --- BEGIN: user implements ---
	int32_t uV;
    if (port_num >= ADC_CHN_MAX || NULL == buff || 0 == len) {
        LOGE("read voltage failed, port_num/%d, buff/%08x, len/%d", port_num, buff, len);
        return OPRT_INVALID_PARM;
    }

    ol_adc_id_enum id = (port_num - TUYA_ADC_NUM_0) + OL_ADC_0;
    for (uint16_t i = 0; i < len; i++) {
        uV = ol_adc_get_vol(id);
		buff[i] = (int32_t)(uV / 1000);
    }
    return OPRT_OK;
    // --- END: user implements ---
}

